mapper.select:
select a.id, a.category_id, category.name AS category_name, a.member_id, a.money, a.description, a.create_time, a.create_user, category.type_id AS type_id, a.member_name, a.account_date, member.name AS record_member_name from account a join member on a.create_user = member.id join category on a.category_id = category.id
and a.category_id = #{categoryId}
and a.family_id = #{familyId}
and category.type_id = #{typeId}
and a.member_id = #{memberId}
and a.description like concat('%', #{description}, '%')
and account_date >= #{beginDate}
and account_date <= #{endDate}
order by a.account_date desc

SELECT CASE WHEN #{groupType} = 'year' THEN DATE_FORMAT(account_date, '%Y') WHEN #{groupType} = 'month' THEN DATE_FORMAT(account_date, '%Y-%m') WHEN #{groupType} = 'week' THEN CONCAT(YEAR(account_date), '-W', WEEK(account_date, 1)) WHEN #{groupType} = 'day' THEN DATE_FORMAT(account_date, '%Y-%m-%d') WHEN #{groupType} = 'member' THEN member_id WHEN #{groupType} = 'category' THEN category_id END AS group_name,
MAX((SELECT m.name FROM member m WHERE m.id = account.member_id)) AS group_display_name,
MAX((SELECT c.name FROM category c WHERE c.id = account.category_id)) AS group_display_name,
SUM(CASE WHEN type_id = 0 THEN money ELSE 0 END) AS income, SUM(CASE WHEN type_id = 1 THEN money ELSE 0 END) AS expense, count(id) AS groupCount FROM account
LEFT JOIN category c ON account.category_id = c.id
and type_id = #{typeId}
AND account_date >= #{beginDate}
AND account_date <= #{endDate}
AND family_id = #{familyId}
AND category_id = #{categoryId}
AND member_id IN
#{memberId}
GROUP BY group_name ORDER BY group_name ASC
